"use strict";(self.webpackChunkcasdoor_website=self.webpackChunkcasdoor_website||[]).push([[8122],{2348:i=>{i.exports=JSON.parse('{"blogPosts":[{"id":"\ud658\uc601\ud569\ub2c8\ub2e4","metadata":{"permalink":"/ko/blog/\ud658\uc601\ud569\ub2c8\ub2e4","editUrl":"https://crowdin.com/project/casdoor-website/ko","source":"@site/i18n/ko/docusaurus-plugin-content-blog/2021-08-24.mdx","title":"\ud658\uc601\ud569\ub2c8\ub2e4","description":"\uce90\uc2a4\ub3c4\uc5b4 \uacf5\uc2dd \uc0ac\uc774\ud2b8\uc5d0 \uc624\uc2e0\uac83\uc744 \ud658\uc601\ud569\ub2c8\ub2e4!\ud83d\udc4b","date":"2021-08-24T00:00:00.000Z","formattedDate":"2021\ub144 8\uc6d4 24\uc77c","tags":[{"label":"\uce90\uc2a4\ub3c4\uc5b4","permalink":"/ko/blog/tags/\uce90\uc2a4\ub3c4\uc5b4"},{"label":"\uac04\ud3b8 \ub85c\uadf8\uc778","permalink":"/ko/blog/tags/\uac04\ud3b8-\ub85c\uadf8\uc778"},{"label":"\uc778\uc99d","permalink":"/ko/blog/tags/\uc778\uc99d"},{"label":"OAuth2.0","permalink":"/ko/blog/tags/o-auth-2-0"},{"label":"\uce90\uc2a4\ube48","permalink":"/ko/blog/tags/\uce90\uc2a4\ube48"}],"readingTime":2.15,"hasTruncateMarker":false,"authors":[{"name":"ErikQQY","title":"\uce90\uc2a4\ube48 \uba64\ubc84","url":"https://github.com/ErikQQY","imageURL":"https://avatars.githubusercontent.com/u/52615090?s=120&v=4"}],"frontMatter":{"slug":"\ud658\uc601\ud569\ub2c8\ub2e4","title":"\ud658\uc601\ud569\ub2c8\ub2e4","author":"ErikQQY","authorTitle":"\uce90\uc2a4\ube48 \uba64\ubc84","authorURL":"https://github.com/ErikQQY","authorImageURL":"https://avatars.githubusercontent.com/u/52615090?s=120&v=4","tags":["\uce90\uc2a4\ub3c4\uc5b4","\uac04\ud3b8 \ub85c\uadf8\uc778","\uc778\uc99d","OAuth2.0","\uce90\uc2a4\ube48"]}},"content":"\uce90\uc2a4\ub3c4\uc5b4 \uacf5\uc2dd \uc0ac\uc774\ud2b8\uc5d0 \uc624\uc2e0\uac83\uc744 \ud658\uc601\ud569\ub2c8\ub2e4!\ud83d\udc4b\\n\\n## Casdoor \uc774\ub780?\\n\\n\uce90\uc2a4\ub3c4\uc5b4(Casdoor)\ub294 OAuth 2.0, OIDC, SAML, CAS\ub97c \uae30\ubc18\uc73c\ub85c \ud558\ub294 UI \ud37c\uc2a4\ud2b8 [\uc544\uc774\ub374\ud2f0\ud2f0 \uc561\uc138\uc2a4 \uad00\ub9ac(IAM)](https://en.wikipedia.org/wiki/Identity_management)/[\uc2f1\uae00 \uc0ac\uc778\uc628(SSO)](https://en.wikipedia.org/wiki/Single_sign-on) \ud50c\ub7ab\ud3fc\uc785\ub2c8\ub2e4.\\n\\n\uce90\uc2a4\ub3c4\uc5b4\ub294 Golang\uacfc React\ub97c \uc0ac\uc6a9\ud558\ub294 [\uce90\uc2a4\ube48](https://casbin.org)\uc744 \uc774\uc6a9\ud574 \uac1c\ubc1c\ub418\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n\uce90\uc2a4\ub3c4\uc5b4\ub294 \uc0ac\uc6a9\uc790 \uad00\ub9ac\ub97c \ucc98\ub9ac\ud560 \uc218 \uc788\ub294 \uac15\ub825\ud55c \uc778\uc99d \ud50c\ub7ab\ud3fc\uc744 \uc81c\uacf5\ud558\ub294 \uac83\uc744 \ubaa9\ud45c\ub85c \ud569\ub2c8\ub2e4.\\n\\n## \uc65c Casdoor\uc744 \uc0ac\uc6a9\ud560\uae4c\uc694?\\n\\n* \u26a1\ufe0f \uac15\ub825\ud55c \uae30\ub2a5\uacfc \uac04\ub2e8\ud55c \uc124\uc815. <img src=\\"https://cdn.casbin.org/img/social_google.png\\" width=\\"30\\"></img> <img src=\\"https://cdn.casbin.org/img/social_github.png\\" width=\\"30\\"></img> <img src=\\"https://cdn.casbin.org/img/social_facebook.png\\" width=\\"30\\"></img> <img src=\\"https://cdn.casbin.org/img/social_twitter.png\\" width=\\"30\\"></img> <img src=\\"https://cdn.casbin.org/img/social_linkedin.png\\" width=\\"30\\"></img> <img src=\\"https://cdn.casbin.org/img/social_weibo.png\\" width=\\"30\\"></img> <img src=\\"https://cdn.casbin.org/img/social_wechat.png\\" width=\\"30\\"></img> <img src=\\"https://cdn.casbin.org/img/social_qq.png\\" width=\\"30\\"></img> <img src=\\"https://cdn.casbin.org/img/social_dingtalk.png\\" width=\\"30\\"></img> <img src=\\"https://cdn.casbin.org/img/social_gitee.png\\" width=\\"30\\"></img> <img src=\\"https://cdn.casbin.org/img/social_wecom.png\\" width=\\"30\\"></img> <img src=\\"https://cdn.casbin.org/img/social_gitlab.png\\" width=\\"30\\"></img> \ub85c\uadf8\uc778\uc744 \uc9c0\uc6d0\ud558\uace0 \uc804\ud1b5\uc801\uc778 \uc0ac\uc6a9\uc790 \uc774\ub984/\ube44\ubc00\ubc88\ud638 \ub85c\uadf8\uc778, Casdoor\ub294 \uba87 \ub2e8\uacc4\ub9cc\uc73c\ub85c \uc0ac\uc6a9\uc790 \uad00\ub9ac \uc2dc\uc2a4\ud15c\uc744 \uad6c\ud604\ud560 \uc218 \uc788\ub3c4\ub85d \ub3c4\uc640\uc90d\ub2c8\ub2e4.\\n\\n* \ud83d\udca5 \ud504\ub860\ud2b8\uc5d4\ub4dc \ubc31\uc5d4\ub4dc \ubd84\ub9ac \uc544\ud0a4\ud14d\ucc98. \uce90\uc2a4\ub3c4\uc5b4\ub294 \uc870\uc791 \uac00\ub2a5\ud55c \uc6f9 UI\uc640 \ub192\uc740 \ub3d9\uc2dc\uc131\uc744 \uc9c0\uc6d0\ud569\ub2c8\ub2e4.\\n\\n* \ud83e\udd42 \ud65c\uc131\ud654\ub41c \uc624\ud508 \uc18c\uc2a4 \ucee4\ubba4\ub2c8\ud2f0. \uce90\uc2a4\ub3c4\uc5b4\ub294 \uc624\ud508 \uc18c\uc2a4\uc774\uba70 \ud65c\ubc1c\ud558\uac8c \uc720\uc9c0\ub429\ub2c8\ub2e4.\\n\\n* \ud83c\udf10 \ub2e4\uc591\ud55c \uc5b8\uc5b4 \uc9c0\uc6d0. \uce90\uc2a4\ub3c4\uc5b4\ub294 \uc911\uad6d\uc5b4, \uc601\uc5b4, \ud504\ub791\uc2a4\uc5b4 \ub4f1\uacfc \uac19\uc740 \ub2e4\uc591\ud55c \uc5b8\uc5b4\ub97c \uc9c0\uc6d0\ud569\ub2c8\ub2e4.\\n\\n\\n## \uce90\uc2a4\ub3c4\uc5b4\ub294 \uc5b4\ub5bb\uac8c \uc0ac\uc6a9\ud558\ub098\uc694?\\n\\n\ub2f9\uc2e0\uc740 [\uce90\uc2a4\ub3c4\uc5b4 API](https://door.casdoor.com/swagger/)\uc640 [\ub2f9\uc2e0\uc758 \uce90\uc2a4\ub3c4\uc5b4 \uc124\uc815\ud558\uae30](/docs/basic/server-installation)\ub97c \uc790\uc138\ud55c \ubb38\uc11c\ub97c \ub530\ub77c\ud574 \ubc30\uc6b8 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ud639\uc740 [\uce90\uc2a4\ub3c4\uc5b4 SDK](/docs/how-to-connect/overview)\ub97c \uc774\uc6a9\ud574 \uc790\uc2e0\uc758 \uc560\ud50c\ub9ac\ucf00\uc774\uc158\uc744 \uad6c\ud604\ud558\uc138\uc694."}]}')}}]);