"use strict";(self.webpackChunkcasdoor_website=self.webpackChunkcasdoor_website||[]).push([[8122],{22348:o=>{o.exports=JSON.parse('{"blogPosts":[{"id":"face-authentication-casdoor-sso","metadata":{"permalink":"/ko/blog/face-authentication-casdoor-sso","editUrl":"https://github.com/casdoor/casdoor-website/edit/master/i18n/ko/docusaurus-plugin-content-blog/2023-12-12.mdx","source":"@site/i18n/ko/docusaurus-plugin-content-blog/2023-12-12.mdx","title":"Casdoor \uc5bc\uad74 \uc778\uc99d SSO\ub85c \uc571 \ubcf4\uc548\uacfc \uc0ac\uc6a9\uc790 \uacbd\ud5d8 \ud5a5\uc0c1","description":"\ub514\uc9c0\ud138 \uc2e0\uc6d0 \uad00\ub9ac\uac00 \uc911\uc694\ud55c \uc2dc\ub300\uc5d0, Casdoor\uc640 \uac19\uc740 Single Sign-On (SSO) \uc194\ub8e8\uc158\uc740 \uc0ac\uc6a9\uc790\uac00 \uc5ec\ub7ec \uc560\ud50c\ub9ac\ucf00\uc774\uc158\uacfc \uc0c1\ud638 \uc791\uc6a9\ud558\ub294 \ubc29\uc2dd\uc744 \ud601\uc2e0\ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4. Casdoor\uc758 SSO\ub294 \uc0ac\uc6a9\uc790\uac00 \ud55c \ubc88\ub9cc \uc778\uc99d\ud558\uc5ec \uc5ec\ub7ec \uc11c\ube44\uc2a4\uc5d0 \uc561\uc138\uc2a4\ud560 \uc218 \uc788\uac8c \ud558\uc5ec \ub85c\uadf8\uc778 \uacfc\uc815\uc744 \ub2e8\uc21c\ud654\ud569\ub2c8\ub2e4. \uc774\ub294 \uc0ac\uc6a9\uc790 \ud3b8\uc758\uc131\uc744 \ud5a5\uc0c1\uc2dc\ud0a4\ub294 \uac83\ubfd0\ub9cc \uc544\ub2c8\ub77c \ucc38\uc5ec\uc640 \ub9cc\uc871\ub3c4\ub97c \ub192\uc785\ub2c8\ub2e4.","date":"2023-12-12T00:00:00.000Z","formattedDate":"2023\ub144 12\uc6d4 12\uc77c","tags":[{"label":"Casdoor","permalink":"/ko/blog/tags/casdoor"},{"label":"SSO","permalink":"/ko/blog/tags/sso"},{"label":"Single-Sign-On","permalink":"/ko/blog/tags/single-sign-on"},{"label":"Face Recogniztion","permalink":"/ko/blog/tags/face-recogniztion"},{"label":"FaceID","permalink":"/ko/blog/tags/face-id"},{"label":"Face","permalink":"/ko/blog/tags/face"},{"label":"\uba38\uc2e0 \ub7ec\ub2dd","permalink":"/ko/blog/tags/\uba38\uc2e0-\ub7ec\ub2dd"}],"readingTime":4.18,"hasTruncateMarker":false,"authors":[{"name":"James Lawson","title":"\uc81c\ud488 \uad00\ub9ac\uc790","url":"https://casdoor.org","imageURL":"https://cdn.casbin.com/casdoor/static/admin.png"}],"frontMatter":{"slug":"face-authentication-casdoor-sso","title":"Casdoor \uc5bc\uad74 \uc778\uc99d SSO\ub85c \uc571 \ubcf4\uc548\uacfc \uc0ac\uc6a9\uc790 \uacbd\ud5d8 \ud5a5\uc0c1","author":"James Lawson","authorTitle":"\uc81c\ud488 \uad00\ub9ac\uc790","authorURL":"https://casdoor.org","authorImageURL":"https://cdn.casbin.com/casdoor/static/admin.png","tags":["Casdoor","SSO","Single-Sign-On","Face Recogniztion","FaceID","Face","\uba38\uc2e0 \ub7ec\ub2dd"]},"nextItem":{"title":"\ud658\uc601\ud569\ub2c8\ub2e4","permalink":"/ko/blog/\ud658\uc601\ud569\ub2c8\ub2e4"}},"content":"\ub514\uc9c0\ud138 \uc2e0\uc6d0 \uad00\ub9ac\uac00 \uc911\uc694\ud55c \uc2dc\ub300\uc5d0, Casdoor\uc640 \uac19\uc740 Single Sign-On (SSO) \uc194\ub8e8\uc158\uc740 \uc0ac\uc6a9\uc790\uac00 \uc5ec\ub7ec \uc560\ud50c\ub9ac\ucf00\uc774\uc158\uacfc \uc0c1\ud638 \uc791\uc6a9\ud558\ub294 \ubc29\uc2dd\uc744 \ud601\uc2e0\ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4. Casdoor\uc758 SSO\ub294 \uc0ac\uc6a9\uc790\uac00 \ud55c \ubc88\ub9cc \uc778\uc99d\ud558\uc5ec \uc5ec\ub7ec \uc11c\ube44\uc2a4\uc5d0 \uc561\uc138\uc2a4\ud560 \uc218 \uc788\uac8c \ud558\uc5ec \ub85c\uadf8\uc778 \uacfc\uc815\uc744 \ub2e8\uc21c\ud654\ud569\ub2c8\ub2e4. \uc774\ub294 \uc0ac\uc6a9\uc790 \ud3b8\uc758\uc131\uc744 \ud5a5\uc0c1\uc2dc\ud0a4\ub294 \uac83\ubfd0\ub9cc \uc544\ub2c8\ub77c \ucc38\uc5ec\uc640 \ub9cc\uc871\ub3c4\ub97c \ub192\uc785\ub2c8\ub2e4.\\n\\n\uae30\uc5c5\ub4e4\uc5d0\uac8c\ub294, Casdoor\uc758 SSO\ub294 \uc0ac\uc6a9\uc790 \uc790\uaca9 \uc99d\uba85 \uad00\ub9ac\ub97c \uac04\uc18c\ud654\ud558\uc5ec \ubcf4\uc548\uc744 \uac15\ud654\ud558\uace0 \uc0c8\ub85c\uc6b4 \uc0ac\uc6a9\uc790 \uc628\ubcf4\ub529\uc744 \ub2e8\uc21c\ud654\ud569\ub2c8\ub2e4. \uc774\ub294 \uc0ac\uc6a9\uc790 \uad00\ub9ac\ub97c \uc911\uc559\ud654\ud568\uc73c\ub85c\uc368 \uc57d\ud55c \ub610\ub294 \uc7ac\uc0ac\uc6a9\ub41c \ube44\ubc00\ubc88\ud638\uc640 \uad00\ub828\ub41c \uc704\ud5d8\uc744 \ub300\uc751\ud569\ub2c8\ub2e4.\\n\\n\uadf8\ub7ec\ub098, \uc628\ub77c\uc778 \uc704\ud611\uc774 \uc99d\uac00\ud558\ub294 \uc0c1\ud669\uc5d0\uc11c, Casdoor\uc640 \uac19\uc740 SSO \uc2dc\uc2a4\ud15c\uc5d0 \uac15\ub825\ud55c \ubcf4\uc548 \uc870\uce58\ub97c \ud1b5\ud569\ud558\ub294 \uac83\uc774 \uc911\uc694\ud569\ub2c8\ub2e4. \uc774\ub54c \uc5bc\uad74 \uc778\uc99d \uae30\uc220\uc774 \uc911\uc694\ud55c \uc5ed\ud560\uc744 \ud558\uac8c \ub418\uba70, \uc0ac\uc6a9\uc790 \uce5c\ud654\uc801\uc778 \uc0c1\ud638 \uc791\uc6a9\uc744 \uc720\uc9c0\ud558\uba74\uc11c \ucd94\uac00\uc801\uc778 \ubcf4\ud638\uce35\uc744 \ucd94\uac00\ud569\ub2c8\ub2e4.\\n\\n\uc5bc\uad74 \uc778\uc99d\uc740 \uace0\uc720\ud55c \uc5bc\uad74 \ud2b9\uc131\uc744 \uc0ac\uc6a9\ud558\uc5ec \uc0ac\uc6a9\uc790 \uc2e0\uc6d0\uc744 \ud655\uc778\ud569\ub2c8\ub2e4. \uc774\ub294 \uc2e4\uc2dc\uac04 \uc774\ubbf8\uc9c0\uc640 \uc800\uc7a5\ub41c \uc5bc\uad74 \ud15c\ud50c\ub9bf\uc744 \ube44\uad50\ud558\uc5ec \uc0ac\uc6a9\uc790\ub97c \uc778\uc99d\ud558\ub294 \uc548\uc804\ud558\uba74\uc11c\ub3c4 \uac04\ub2e8\ud55c \ubc29\ubc95\uc744 \uc81c\uacf5\ud569\ub2c8\ub2e4. \uc774 \ubc29\ubc95\uc740 \ub2e4\uc591\ud55c \uc870\uac74\uc5d0\uc11c\ub3c4 \ub192\uc740 \uc815\ud655\ub3c4\ub97c \uc790\ub791\ud558\uba70, \ud604\ub300 \uc18c\ube44\uc790 \uc7a5\uce58\uc5d0\uc11c \ub110\ub9ac \ucc44\ud0dd\ub418\uace0 \uc788\uc2b5\ub2c8\ub2e4.\\n\\nCasdoor\uc758 SSO \uc2dc\uc2a4\ud15c\uc5d0 \uc5bc\uad74 \uc778\uc99d\uc744 \ud1b5\ud569\ud558\uba74 \uc548\uc804\ud558\uace0 \ud6a8\uc728\uc801\uc774\uba70 \uc6d0\ud65c\ud55c \uc0ac\uc6a9\uc790 \uacbd\ud5d8\uc744 \uc57d\uc18d\ud569\ub2c8\ub2e4. \ucd5c\uc801\uc758 \uc131\ub2a5\uc744 \ubcf4\uc7a5\ud558\uae30 \uc704\ud574\uc11c\ub294 \ub2e4\uc74c\uc5d0 \uc9d1\uc911\ud558\ub294 \uac83\uc774 \uc911\uc694\ud569\ub2c8\ub2e4:\\n\\n1. _\uc2a4\ud478\ud551 \ubc29\uc9c0 \uc870\uce58_: \ub208\ub3d9\uc790 \uc6c0\uc9c1\uc784 \ucd94\uc801\uc774\ub098 \ubb34\uc791\uc704 \uc81c\uc2a4\ucc98\uc640 \uac19\uc740 \uc0dd\uccb4 \uc778\uc2dd\uc744 \uad6c\ud604\ud558\ub294 \uac83\uc740 \uc2a4\ud478\ud551 \uc2dc\ub3c4\ub97c \ub300\uc751\ud558\uace0 \uc2dc\uc2a4\ud15c \ubb34\uacb0\uc131\uc744 \uc720\uc9c0\ud558\ub294 \ub370 \ud544\uc218\uc801\uc785\ub2c8\ub2e4.\\n\\n2. _\uc815\ud655\uc131 \ud5a5\uc0c1\uc744 \uc704\ud55c \uc815\uae30\uc801\uc778 \uc5c5\ub370\uc774\ud2b8_: \ucd5c\uc2e0 AI\uc640 \uc0dd\uccb4 \uc778\uc2dd \uae30\uc220\ub85c \uc5bc\uad74 \uc778\uc99d \uae30\uc220\uc744 \uc5c5\ub370\uc774\ud2b8\ud558\ub294 \uac83\uc740 \uc815\ud655\uc131\uc744 \ubcf4\uc7a5\ud558\uace0 \uc0ac\uc6a9\uc790 \uacbd\ud5d8\uc744 \ud5a5\uc0c1\uc2dc\ud0b5\ub2c8\ub2e4. \uc54c\uace0\ub9ac\uc998 \ud6c8\ub828\uc744 \uc704\ud574 \ub2e4\uc591\ud55c \ub370\uc774\ud130\uc14b\uc744 \uc0ac\uc6a9\ud558\ub294 \uac83\uc774 \ud3b8\ud5a5\uc744 \ubc29\uc9c0\ud558\ub294 \ub370 \uc911\uc694\ud569\ub2c8\ub2e4.\\n\\n3. _\ubcf4\uc548\uacfc \ud3b8\uc758\uc131\uc758 \uade0\ud615_: \ucd5c\uace0\uc758 \uc2dc\uc2a4\ud15c\uc740 \uc5c4\uaca9\ud55c \ubcf4\uc548 \uc870\uce58\uc640 \uc0ac\uc6a9\uc790 \ud3b8\uc758\uc131 \uc0ac\uc774\uc5d0 \uc644\ubcbd\ud55c \uade0\ud615\uc744 \uc774\ub8f9\ub2c8\ub2e4. \uc774 \uc811\uadfc \ubc29\uc2dd\uc740 \ubcf4\uc548\uc744 \ud0c0\ud611\ud558\uc9c0 \uc54a\ub294 \uac04\ub2e8\ud55c \uc778\uc99d \uacfc\uc815\uc744 \ubcf4\uc7a5\ud568\uc73c\ub85c\uc368 \uc0ac\uc6a9\uc790 \ucc44\ud0dd\uc744 \ucd09\uc9c4\ud569\ub2c8\ub2e4.\\n\\n\uacb0\ub860\uc801\uc73c\ub85c, \uc5bc\uad74 \uc778\uc99d\uc744 Casdoor\uc758 SSO\uc5d0 \ud1b5\ud569\ud568\uc73c\ub85c\uc368, \uae30\uc5c5\ub4e4\uc740 \uc548\uc804\ud558\uace0 \ud6a8\uc728\uc801\uc774\uba70 \uc0ac\uc6a9\uc790 \uce5c\ud654\uc801\uc778 \ub85c\uadf8\uc778 \uacbd\ud5d8\uc744 \uc81c\uacf5\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc774\ub294 \ubcf4\uc548\uc744 \uac15\ud654\ud558\ub294 \uac83\ubfd0\ub9cc \uc544\ub2c8\ub77c \ub514\uc9c0\ud138 \uc138\uacc4\uc5d0\uc11c\uc758 \ud604\ub300 \uc0ac\uc6a9\uc790 \uae30\ub300\uce58\uc640 \uc77c\uce58\uc2dc\ud0b5\ub2c8\ub2e4."},{"id":"\ud658\uc601\ud569\ub2c8\ub2e4","metadata":{"permalink":"/ko/blog/\ud658\uc601\ud569\ub2c8\ub2e4","editUrl":"https://github.com/casdoor/casdoor-website/edit/master/i18n/ko/docusaurus-plugin-content-blog/2021-08-24.mdx","source":"@site/i18n/ko/docusaurus-plugin-content-blog/2021-08-24.mdx","title":"\ud658\uc601\ud569\ub2c8\ub2e4","description":"\uce90\uc2a4\ub3c4\uc5b4 \uacf5\uc2dd \uc0ac\uc774\ud2b8\uc5d0 \uc624\uc2e0\uac83\uc744 \ud658\uc601\ud569\ub2c8\ub2e4!\ud83d\udc4b","date":"2021-08-24T00:00:00.000Z","formattedDate":"2021\ub144 8\uc6d4 24\uc77c","tags":[{"label":"\uce90\uc2a4\ub3c4\uc5b4","permalink":"/ko/blog/tags/\uce90\uc2a4\ub3c4\uc5b4"},{"label":"\uac04\ud3b8 \ub85c\uadf8\uc778","permalink":"/ko/blog/tags/\uac04\ud3b8-\ub85c\uadf8\uc778"},{"label":"\uc778\uc99d","permalink":"/ko/blog/tags/\uc778\uc99d"},{"label":"OAuth2.0","permalink":"/ko/blog/tags/o-auth-2-0"},{"label":"\uce90\uc2a4\ube48","permalink":"/ko/blog/tags/\uce90\uc2a4\ube48"}],"readingTime":2.145,"hasTruncateMarker":false,"authors":[{"name":"ErikQQY","title":"\uce90\uc2a4\ube48 \uba64\ubc84","url":"https://github.com/ErikQQY","imageURL":"https://avatars.githubusercontent.com/u/52615090?s=120&v=4"}],"frontMatter":{"slug":"\ud658\uc601\ud569\ub2c8\ub2e4","title":"\ud658\uc601\ud569\ub2c8\ub2e4","author":"ErikQQY","authorTitle":"\uce90\uc2a4\ube48 \uba64\ubc84","authorURL":"https://github.com/ErikQQY","authorImageURL":"https://avatars.githubusercontent.com/u/52615090?s=120&v=4","tags":["\uce90\uc2a4\ub3c4\uc5b4","\uac04\ud3b8 \ub85c\uadf8\uc778","\uc778\uc99d","OAuth2.0","\uce90\uc2a4\ube48"]},"prevItem":{"title":"Casdoor \uc5bc\uad74 \uc778\uc99d SSO\ub85c \uc571 \ubcf4\uc548\uacfc \uc0ac\uc6a9\uc790 \uacbd\ud5d8 \ud5a5\uc0c1","permalink":"/ko/blog/face-authentication-casdoor-sso"}},"content":"\uce90\uc2a4\ub3c4\uc5b4 \uacf5\uc2dd \uc0ac\uc774\ud2b8\uc5d0 \uc624\uc2e0\uac83\uc744 \ud658\uc601\ud569\ub2c8\ub2e4!\ud83d\udc4b\\n\\n## Casdoor \uc774\ub780?\\n\\n\uce90\uc2a4\ub3c4\uc5b4(Casdoor)\ub294 OAuth 2.0, OIDC, SAML, CAS\ub97c \uae30\ubc18\uc73c\ub85c \ud558\ub294 UI \ud37c\uc2a4\ud2b8 [\uc544\uc774\ub374\ud2f0\ud2f0 \uc561\uc138\uc2a4 \uad00\ub9ac(IAM)](https://en.wikipedia.org/wiki/Identity_management)/[\uc2f1\uae00 \uc0ac\uc778\uc628(SSO)](https://en.wikipedia.org/wiki/Single_sign-on) \ud50c\ub7ab\ud3fc\uc785\ub2c8\ub2e4.\\n\\n\uce90\uc2a4\ub3c4\uc5b4\ub294 Golang\uacfc React\ub97c \uc0ac\uc6a9\ud558\ub294 [\uce90\uc2a4\ube48](https://casbin.org)\uc744 \uc774\uc6a9\ud574 \uac1c\ubc1c\ub418\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n\uce90\uc2a4\ub3c4\uc5b4\ub294 \uc0ac\uc6a9\uc790 \uad00\ub9ac\ub97c \ucc98\ub9ac\ud560 \uc218 \uc788\ub294 \uac15\ub825\ud55c \uc778\uc99d \ud50c\ub7ab\ud3fc\uc744 \uc81c\uacf5\ud558\ub294 \uac83\uc744 \ubaa9\ud45c\ub85c \ud569\ub2c8\ub2e4.\\n\\n## \uc65c Casdoor\uc744 \uc0ac\uc6a9\ud560\uae4c\uc694?\\n\\n* \u26a1\ufe0f \uac15\ub825\ud55c \uae30\ub2a5\uacfc \uac04\ub2e8\ud55c \uc124\uc815. <img src=\\"https://cdn.casbin.org/img/social_google.png\\" width=\\"30\\"></img> <img src=\\"https://cdn.casbin.org/img/social_github.png\\" width=\\"30\\"></img> <img src=\\"https://cdn.casbin.org/img/social_facebook.png\\" width=\\"30\\"></img> <img src=\\"https://cdn.casbin.org/img/social_twitter.png\\" width=\\"30\\"></img> <img src=\\"https://cdn.casbin.org/img/social_linkedin.png\\" width=\\"30\\"></img> <img src=\\"https://cdn.casbin.org/img/social_weibo.png\\" width=\\"30\\"></img> <img src=\\"https://cdn.casbin.org/img/social_wechat.png\\" width=\\"30\\"></img> <img src=\\"https://cdn.casbin.org/img/social_qq.png\\" width=\\"30\\"></img> <img src=\\"https://cdn.casbin.org/img/social_dingtalk.png\\" width=\\"30\\"></img> <img src=\\"https://cdn.casbin.org/img/social_gitee.png\\" width=\\"30\\"></img> <img src=\\"https://cdn.casbin.org/img/social_wecom.png\\" width=\\"30\\"></img> <img src=\\"https://cdn.casbin.org/img/social_gitlab.png\\" width=\\"30\\"></img> \ub85c\uadf8\uc778\uc744 \uc9c0\uc6d0\ud558\uace0 \uc804\ud1b5\uc801\uc778 \uc0ac\uc6a9\uc790 \uc774\ub984/\ube44\ubc00\ubc88\ud638 \ub85c\uadf8\uc778, Casdoor\ub294 \uba87 \ub2e8\uacc4\ub9cc\uc73c\ub85c \uc0ac\uc6a9\uc790 \uad00\ub9ac \uc2dc\uc2a4\ud15c\uc744 \uad6c\ud604\ud560 \uc218 \uc788\ub3c4\ub85d \ub3c4\uc640\uc90d\ub2c8\ub2e4.\\n\\n* \ud83d\udca5 \ud504\ub860\ud2b8\uc5d4\ub4dc \ubc31\uc5d4\ub4dc \ubd84\ub9ac \uc544\ud0a4\ud14d\ucc98. \uce90\uc2a4\ub3c4\uc5b4\ub294 \uc870\uc791 \uac00\ub2a5\ud55c \uc6f9 UI\uc640 \ub192\uc740 \ub3d9\uc2dc\uc131\uc744 \uc9c0\uc6d0\ud569\ub2c8\ub2e4.\\n\\n* \ud83e\udd42 \ud65c\uc131\ud654\ub41c \uc624\ud508 \uc18c\uc2a4 \ucee4\ubba4\ub2c8\ud2f0. \uce90\uc2a4\ub3c4\uc5b4\ub294 \uc624\ud508 \uc18c\uc2a4\uc774\uba70 \ud65c\ubc1c\ud558\uac8c \uc720\uc9c0\ub429\ub2c8\ub2e4.\\n\\n* \ud83c\udf10 \ub2e4\uc591\ud55c \uc5b8\uc5b4 \uc9c0\uc6d0. \uce90\uc2a4\ub3c4\uc5b4\ub294 \uc911\uad6d\uc5b4, \uc601\uc5b4, \ud504\ub791\uc2a4\uc5b4 \ub4f1\uacfc \uac19\uc740 \ub2e4\uc591\ud55c \uc5b8\uc5b4\ub97c \uc9c0\uc6d0\ud569\ub2c8\ub2e4.\\n\\n\\n## \uce90\uc2a4\ub3c4\uc5b4\ub294 \uc5b4\ub5bb\uac8c \uc0ac\uc6a9\ud558\ub098\uc694?\\n\\n\ub2f9\uc2e0\uc740 [\uce90\uc2a4\ub3c4\uc5b4 API](https://door.casdoor.com/swagger/)\uc640 [\ub2f9\uc2e0\uc758 \uce90\uc2a4\ub3c4\uc5b4 \uc124\uc815\ud558\uae30](/docs/basic/server-installation)\ub97c \uc790\uc138\ud55c \ubb38\uc11c\ub97c \ub530\ub77c\ud574 \ubc30\uc6b8 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ub610\ub294 [Casdoor SDKs](/docs/how-to-connect/sdk)\ub97c \uc0ac\uc6a9\ud558\uc5ec \uc790\uc2e0\ub9cc\uc758 \uc560\ud50c\ub9ac\ucf00\uc774\uc158\uc744 \uad6c\ud604\ud558\uc138\uc694."}]}')}}]);